import React, { useState } from 'react';
import './POSController.css';

function POSController() {
    const [items] = useState([
        { id: 1, name: 'Water', category: 'Drinks', price: 1.5 },
        { id: 2, name: 'Soda', category: 'Drinks', price: 2.0 },
        { id: 3, name: 'Juice', category: 'Drinks', price: 2.5 },
        { id: 4, name: 'Energy Drink', category: 'Drinks', price: 3.0 },
        // Add more items and categories
    ]);
    const [category, setCategory] = useState('Drinks');
    const [order, setOrder] = useState([]);

    const filteredItems = items.filter((item) => item.category === category);

    const addItemToOrder = (item) => {
        setOrder([...order, item]);
    };

    return (
        <div>
            <h3>Items in {category}</h3>
            <div className="items-grid">
                {filteredItems.map((item) => (
                    <button
                        key={item.id}
                        onClick={() => addItemToOrder(item)}
                        className="item-button"
                    >
                        {item.name} - ${item.price}
                    </button>
                ))}
            </div>
        </div>
    );
}

export default POSController;
