import React from 'react';
import './OrderSummary.css';

function OrderSummary({ order, total, clearOrder }) {
    return (
        <div className="order-summary">
            <h2>Order Summary</h2>
            <ul>
                {order.map((item) => (
                    <div
                        key={item.id}
                        onClick={() => {
                            setSelectedItem(item);
                            console.log('Clicked item:', item);
                        }}
                        style={{
                            cursor: 'pointer',
                            backgroundColor: selectedItem?.id === item.id ? 'lightblue' : 'transparent',
                        }}
                    >
                        {item.name} x {item.quantity} - ${item.itemTotal.toFixed(2)}
                    </div>
                ))}
                {order.map((item, index) => (
                    <li key={index}>
                        <button>
                            {item.quantity} x {item.name}: ${item.itemTotal.toFixed(2)}
                        </button>
                    </li>
                ))}
            </ul>
            <h3>Total: ${total.toFixed(2)}</h3>
            <button onClick={clearOrder}>Save Order</button> /* implement saveOrder function */
        </div>
    );
}

export default OrderSummary;

