import React, { useState } from 'react';
import './POSController.css';

function POSController({ selectedCategory }) {
    const [items] = useState([
        { id: 1, name: 'Water', category: 'Drinks', price: 1.5 },
        { id: 2, name: 'Soda', category: 'Drinks', price: 2.0 },
        { id: 3, name: 'Juice', category: 'Drinks', price: 2.5 },
        { id: 4, name: 'Chips', category: 'Snacks', price: 1.0 },
        { id: 5, name: 'Candy', category: 'Snacks', price: 0.5 },
        { id: 6, name: 'Burger', category: 'Meals', price: 5.0 },
        { id: 7, name: 'Pizza', category: 'Meals', price: 8.0 },
    ]);
    const [order, setOrder] = useState([]);

    const filteredItems = items.filter((item) => item.category === selectedCategory);

    const addItemToOrder = (item) => {
        setOrder([...order, item]);
    };

    return (
        <div>
            <div className="items-grid">
                {filteredItems.map((item) => (
                    <button
                        key={item.id}
                        onClick={() => addItemToOrder(item)}
                        className="item-button"
                    >
                        {item.name} - ${item.price.toFixed(2)}
                    </button>
                ))}
            </div>
        </div>
    );
}

export default POSController;
