import React, { useState } from 'react';
import POSController from './POSController';
import './App.css';

function App() {
    const [selectedCategory, setSelectedCategory] = useState('Drinks'); // Default category

    const categories = ['Drinks', 'Snacks', 'Meals']; // List of categories

    return (
        <div className="App">
            <header className="app-header">
                <h1>POS System</h1>
                <div className="username">Welcome, UserName</div>
            </header>
            <main className="main-layout">
                <section className="categories-section">
                    <h2>Categories</h2>
                    <ul>
                        {categories.map((category) => (
                            <li
                                key={category}
                                className={category === selectedCategory ? 'active-category' : ''}
                                onClick={() => setSelectedCategory(category)}
                            >
                                {category}
                            </li>
                        ))}
                    </ul>
                </section>
                <section className="items-section">
                    <h2>Items</h2>
                    <POSController selectedCategory={selectedCategory} />
                </section>
                <section className="order-summary-section">
                    <h2>Order Summary Terminal</h2>
                    <div id="order-summary"></div>
                </section>
                <section className="admin-section">
                    <h2>Administrator Options</h2>
                    <button>Edit Menu</button>
                    <button>View Reports</button>
                </section>
            </main>
        </div>
    );
}

export default App;
