import React from 'react';

function Keypad({ quantity, setQuantity }) {
    const handleKeyPress = (number) => {
        if (number === 'Clear') {
            setQuantity(0);
        } else if (number === 'Enter') {
            // Enter is handled when adding items, so do nothing here
        } else {
            setQuantity(parseInt(`${quantity}${number}`, 10));
        }
    };

    const rows = [
        ['1', '2', '3'],
        ['4', '5', '6'],
        ['7', '8', '9'],
        ['Clear', '0']
        ['Clear', '0', 'Enter']
    ];

    return (
        <div className="keypad">
            {rows.map((row, rowIndex) => (
                <div key={rowIndex} className="keypad-row">
                    {row.map((num) => (
                        <button key={num} onClick={() => handleKeyPress(num)}>
                            {num}
                        </button>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default Keypad;
